(42, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-analytics-order-attribution-promotions-december-2024', 'marketing', 'en_US', 'Discover what drives your sales', 'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'payson-retirement-2025', 'update', 'en_US', 'Payson Form Retirement', 'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2025-05-29 20:09:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cách tùy chỉnh danh mục sản phẩm của bạn', 'Bạn muốn danh mục sản phẩm và hình ảnh của mình trông đẹp mắt và phù hợp với thương hiệu của bạn. Hướng dẫn này sẽ cung cấp cho bạn tất cả các mẹo bạn cần để sản phẩm của bạn trông đẹp mắt trong cửa hàng của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2025-06-03 08:40:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-launch-checklist', 'info', 'en_US', 'Bạn đã sẵn sàng để ra mắt cửa hàng của mình?', 'Để đảm bảo bạn không bao giờ có cảm giác chìm đắm \"mình đã quên điều gì\", chúng tôi đã tập hợp danh sách kiểm tra trước khi ra mắt cần thiết.', '{}', 'unactioned', 'woocommerce-admin', '2025-06-03 08:40:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Bảo vệ Của hàng WooCommerce của bạn bằng Jetpack Backup.', 'Thời gian ngừng hoạt động của cửa hàng đồng nghĩa với việc mất doanh thu. Khôi phục bằng một cú nhấp chuột giúp bạn nhanh chóng hoạt động trở lại nếu có sự cố xảy ra.', '{}', 'unactioned', 'woocommerce-admin-notes', '2025-06-03 08:40:19', NULL, 0, 'thumbnail', 'http://localhost/wordpress/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(64, 'wc-admin-mobile-app', 'info', 'en_US', 'Cài đặt ứng dụng di động Woo', 'Cài đặt ứng dụng di động WooCommerce để quản lý đơn đặt hàng, nhận thông báo bán hàng và xem các số liệu chính - mọi lúc mọi nơi.', '{}', 'unactioned', 'woocommerce-admin', '2025-06-03 08:40:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Bắt đầu chấp nhận thanh toán trên cửa hàng của bạn!', 'Nhận thanh toán với nhà cung cấp phù hợp với bạn - chọn từ hơn 100 cổng thanh toán cho WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2025-06-04 11:49:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'tento_q2_2025', 'marketing', 'en_US', 'Get your free business credit score', 'Strong business credit is the key to better deals, stronger partnerships, and sustainable growth. Tento Business Credit Monitoring helps you catch risks early, secure better financing, and fuel smarter growth — with no impact to your credit.', '{}', 'pending', 'woocommerce.com', '2025-06-04 11:50:03', NULL, 0, 'plain', '', 0, 0, 'info');
